package com.koreait.app.payback.vo;

public class PaybackVO {
	private String p_id;
	private String p_name;
	private String p_phone;
	private String p_email;
	private String p_zipcode;
	private String p_address;
	private String p_address_detail;
	private String p_request;
	private String p_order;
	private int p_point;

	public PaybackVO() {
		// TODO Auto-generated constructor stub
	}

	public String getP_id() {
		return p_id;
	}

	public void setP_id(String p_id) {
		this.p_id = p_id;
	}

	public String getP_name() {
		return p_name;
	}

	public void setP_name(String p_name) {
		this.p_name = p_name;
	}

	public String getP_phone() {
		return p_phone;
	}

	public void setP_phone(String p_phone) {
		this.p_phone = p_phone;
	}

	public String getP_email() {
		return p_email;
	}

	public void setP_email(String p_email) {
		this.p_email = p_email;
	}

	public String getP_zipcode() {
		return p_zipcode;
	}

	public void setP_zipcode(String p_zipcode) {
		this.p_zipcode = p_zipcode;
	}

	public String getP_address() {
		return p_address;
	}

	public void setP_address(String p_address) {
		this.p_address = p_address;
	}

	public String getP_address_detail() {
		return p_address_detail;
	}

	public void setP_address_detail(String p_address_detail) {
		this.p_address_detail = p_address_detail;
	}

	public String getP_request() {
		return p_request;
	}

	public void setP_request(String p_request) {
		this.p_request = p_request;
	}

	public String getP_order() {
		return p_order;
	}

	public void setP_order(String p_order) {
		this.p_order = p_order;
	}
	
	public int getP_point() {
		return p_point;
	}

	public void setP_point(int p_point) {
		this.p_point = p_point;
	}

}


